// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;


/**
 * @title Airdrop Distribution Contract
 * @dev Contract for distributing airdrop tokens to eligible recipients.
 */
abstract contract Airdrop {
    uint8 private constant airdropDistributionAmount = 25;


    // Mapping to track whether the recipient has claimed the airdrop
    mapping(address => uint) private airdropRecipients;

    // Mapping to track total token transfers for each recipient
    mapping(address => uint) private totalTransfers;

    // Maximum allowed tokens to be distributed as airdrop
    uint private _airdropLimit;

    // Total tokens withdrawn in the airdrop
    uint private _totalTokenWithdrawn;

    /**
     * @dev Constructor to initialize the airdrop contract with a maximum limit.
     * @param airdropLimit Maximum allowed tokens for airdrop.
     */
    constructor(uint airdropLimit){
        _airdropLimit = airdropLimit;
    }

    /**
     * @dev Get the maximum allowed tokens for airdrop.
     * @return Maximum allowed tokens for airdrop.
     */
    function getAirdropLimit() public view returns(uint){
        return _airdropLimit;
    }

    /**
     * @dev Function to check if a recipient can claim the airdrop and perform the claim.
     * @param recipient Address of the recipient.
     * @return Whether the airdrop claim was successful.
     */
    function claimAirdrop(address recipient) public payable returns(bool){
        // Check if the airdrop limit has been reached
        require(_totalTokenWithdrawn < _airdropLimit, "Airdrop sale is over :(!");

        // Check if the recipient has already claimed the airdrop
        require(airdropRecipients[recipient] == 0, "Airdrop already claimed!");

        // Assign airdrop tokens to the recipient
        airdropRecipients[recipient] = airdropDistributionAmount;

        return true;
    }

    function getAirdropDistributionAmount() internal pure returns(uint){
        return airdropDistributionAmount;
    }

    /**
     * @dev Function to check if a recipient has received the airdrop.
     * @param recipient Address of the recipient.
     * @return Whether the recipient has received the airdrop.
     */
    function hasReceivedAirdrop(address recipient) internal view returns(bool){
        return airdropRecipients[recipient] > 0;
    }

    /**
     * @dev Function to get the total transfer amount for a recipient.
     * @param recipient Address of the recipient.
     * @return Total transfer amount for the recipient.
     */
    function getTotalAirdropTransfer(address recipient) internal view returns(uint){
        return totalTransfers[recipient];
    }

    /**
     * @dev Function to update the total Transfers for a account.
     * @param addr Address of the recipient.
     */
    function updateTotalTransfers(address addr, uint amount) internal{
        totalTransfers[addr] += amount;
    }

}

